        [Test]
        public void Day1_A()
        {
            var day1 = new Day1();

            using (Assert.EnterMultipleScope())
            {
                Assert.That(day1.CalculateFloor("(())"), Is.EqualTo(0));
                Assert.That(day1.CalculateFloor("()()"), Is.EqualTo(0));

                Assert.That(day1.CalculateFloor("((("), Is.EqualTo(3));
                Assert.That(day1.CalculateFloor("(()(()("), Is.EqualTo(3));
                Assert.That(day1.CalculateFloor("))((((("), Is.EqualTo(3));

                Assert.That(day1.CalculateFloor("())"), Is.EqualTo(-1));
                Assert.That(day1.CalculateFloor("))("), Is.EqualTo(-1));

                Assert.That(day1.CalculateFloor(")))"), Is.EqualTo(-3));
                Assert.That(day1.CalculateFloor(")())())"), Is.EqualTo(-3));

                Assert.That(day1.CalculateFloor(Input), Is.EqualTo(232));
            }
        }

        [Test]
        public void Day1_B()
        {
            var day1 = new Day1();

            Assert.That(day1.CalculateFirstBasementOccurancePosition(Input), Is.EqualTo(1783));
        }
